/**
* Links e as last element(链接e最为最后一个元素).
*/
void linkLast(E e) {
    // 把当前的DLL（双向链表）表中最后一个元素给l
    final Node l = last;
    // 创建一个新节点 e, e的prev指针指向l，e的next指针指向null
    // 也就是说e是最后一个元素，last = e
    final Node newNode = new Node<>(l, e, null);
    last = newNode;
    // 如果DLL的最后一个元素是null，说明当前就是第0个，所以first = last = e
    if (l == null)
        first = newNode;
    else
        l.next = newNode;
    size++;
    modCount++;
}

