public boolean addAll(int index, Collection c) {
    checkPositionIndex(index);

    Object[] a = c.toArray();

    int numNew = a.length;

    if (numNew == 0){
        return false;
    }
    // 定义了两个Node对象 pred 和succ
    // pred 要插入结点的前一个结点
    // succ 要插入结点的后一个结点，也就是node(index)的那个值
    Node pred, succ;
    if (index == size) {
        // 可以理解为两个LinkedList，List1为新建的对象或有值的对象，List2为有值的对象
        //List1.addAll(size,List2)
        // succ就不存
        succ = null;
        // pred节点为当前最后一个节点
        pred = last;
    } else {
        // 获取index下标的节点
        succ = node(index);
        // pred为succ节点的前一个节点
        pred = succ.prev;
    }
    // 循环将数据进行链接
    for (Object o : a) {
        @SuppressWarnings("unchecked") 
        E e = (E) o;
        Node newNode = new Node<>(pred, e, null);
        if (pred == null)
            first = newNode;
        else
            pred.next = newNode;
        pred = newNode;
    }
    if (succ == null) {
        // 也就是addAll(size,List),最后一个就为最后链接的node结点
        last = pred;
    } else {
        // 否则就要把后面断开的链接上，形成双向链表
        pred.next = succ;
        succ.prev = pred;
    }
    // 更新List的长度值size
    size += numNew;
    modCount++;
    return true;
}




remove方法源码解析
~~~java
// 移除index的节点
public E remove(int index) {
// 下标检查
// index >= 0 && index }


  public boolean remove(Object o) {
      if (o == null) {
          for (Node x = first; x != null; x = x.next) {
              if (x.item == null) {
                  unlink(x);
                  return true;
              }
          }
      } else {
          for (Node x = first; x != null; x = x.next) {
              if (o.equals(x.item)) {
                  unlink(x);
                  return true;
              }
          }
      }
      return false;
  }
  E unlink(Node x) {
      // assert x != null;
      // 获取节点的值， 要删除的节点
      final E element = x.item;
      // x的后继节点
      final Node next = x.next;
      // x的前继节点
      final Node prev = x.prev;
  if (prev == null) {
      // 没有前继节点，那么就是第一个节点对象
      // 头部指向x删除节点的后继节点
      first = next;
  } else {
      // 删除元素的前继节点-->删除元素的后继节点
      prev.next = next;
      // 清空删除节点的前继指向
      x.prev = null;
  }
  // 删除节点的后继节点=null
  if (next == null) {
      //尾部指向删除节点的前继节点
      last = prev;
  } else {
      //删除节点的后继节点，指向删除节点的前继节点
      next.prev = prev;
      // 清空删除节点x的后继指向
      x.next = null;
  }

  x.item = null;//便于GC
  // 更新size值
  size--;
  modCount++;
  return element;
  }



