  /**
   * Links e as first element.
   */
  private void linkFirst(E e) {
      final Node f = first;
      final Node newNode = new Node<>(null, e, f);
      first = newNode;
      if (f == null)
          last = newNode;
      else
          f.prev = newNode;
      size++;
      modCount++;
  }

  /**
    * Links e as last element.
    */
  void linkLast(E e) {
      final Node l = last;
      final Node newNode = new Node<>(l, e, null);
      last = newNode;
      if (l == null)
          first = newNode;
      else
          l.next = newNode;
      size++;
      modCount++;
  }

